mapper.select:
select post_id
from TB_POST_HATE
where user_id = #{user_id}

SELECT EXISTS (
SELECT 1
FROM TB_POST_LIKE
WHERE user_id = #{user_id} AND post_id = #{post_id}
) AS result

mapper.insert:
INSERT INTO TB_POST_LIKE (user_id, post_id)
SELECT #{user_id}, post_id
FROM TB_POST
WHERE post_id = #{post_id}
AND delete_status = 0;

mapper.delete:
delete
from TB_POST_LIKE
where user_id = #{user_id} and post_id = #{post_id}

mapper.select:
SELECT EXISTS (
SELECT 1
FROM TB_POST_HATE
WHERE user_id = #{user_id} AND post_id = #{post_id}
) AS result

mapper.insert:
INSERT INTO TB_POST_HATE (user_id, post_id)
SELECT #{userId}, post_id
FROM TB_POST
WHERE post_id = #{post_id}
AND delete_status = 0;

mapper.delete:
delete
from TB_POST_HATE
where user_id = #{user_id} and post_id = #{post_id}

mapper.update:
UPDATE TB_POST
SET like_count = like_count - 1
WHERE post_id = #{post_id};

UPDATE TB_POST
SET like_count = like_count + 1
WHERE post_id = #{post_id};

UPDATE TB_POST
SET hate_count = hate_count - 1
WHERE post_id = #{post_id};

UPDATE TB_POST
SET hate_count = hate_count + 1
WHERE post_id = #{post_id};
