mapper.select:
select post_id
from TB_POST_HATE
where user_id = #{userId}

SELECT EXISTS (
SELECT 1
FROM TB_POST_LIKE
WHERE user_id = #{userId} AND post_id = #{postId}
) AS result

mapper.insert:
INSERT INTO TB_POST_LIKE (user_id, post_id)
SELECT #{userId}, post_id
FROM TB_POST
WHERE post_id = #{postId}
AND delete_status = 0;

mapper.delete:
delete
from TB_POST_LIKE
where user_id = #{userId} and post_id = #{postId}

mapper.select:
SELECT EXISTS (
SELECT 1
FROM TB_POST_HATE
WHERE user_id = #{userId} AND post_id = #{postId}
) AS result

mapper.insert:
INSERT INTO TB_POST_HATE (user_id, post_id)
SELECT #{userId}, post_id
FROM TB_POST
WHERE post_id = #{postId}
AND delete_status = 0;

mapper.delete:
delete
from TB_POST_HATE
where user_id = #{userId} and post_id = #{postId}
