mapper.select:
select user_id, user_email, pw, user_name, provider_type, role_Type
from TB_USER
where user_id = #{user_id}

mapper.update:
update TB_USER
set user_name = #{user_name}
where user_id = #{user_id}

update TB_USER
set pw = #{pw}
where user_id = #{user_id}

update TB_USER
set phone_number = #{phone_number}, image_url = #{image_url}
where user_id = #{user_id}
