mapper.select:
select user_id, user_email, pw, user_name, provider_type, role_type
from TB_USER
where user_email = #{user_email}

select user_id, email, password, username, provider_type, role_type
from TB_USER
where user_id = #{user_id}

mapper.update:
update TB_USER
set username = #{username}
where user_id = #{user_id}

update TB_USER
set password = #{pw}
where user_id = #{user_id}
